viction-runs-millis: 6000\n        min-evictable-idle-time-millis: 300000\n        filters: stat\n        break-after-acquire-failure: false\n\n    # druid monitor configuration\n    druid:\n      stat-view-servlet:\n        enabled: true\n        login-username: admin\n        login-password: 123456\n        allow: \"\"\n\n  #########################################\n  # jackson configuration\n  #########################################\n  jackson:\n    time-zone: GMT+8\n    date-format: yyyy-MM-dd HH:mm:ss\n    locale: zh_CN\n\n  #########################################\n  # redis configuration\n  #########################################\n  data:\n    redis:\n      database: 1\n      host: 127.0.0.1\n      port: 6379\n      password:\n      timeout: 10s\n      lettuce:\n        pool:\n          max-active: 200\n          max-wait: -1ms\n          max-idle: 10\n          min-idle: 0\n\n#########################################\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\n#########################################\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n    health:\n      show-details: \"ALWAYS\"\n\n#########################################\n# mybatis-plus configuration\n#########################################\nmybatis-plus:\n  configuration:\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n  global-config:\n    banner: false\n    enable-sql-runner: true\n    db-config:\n      id-type: ASSIGN_ID\n      logic-delete-field: DELETE_FLAG\n      logic-delete-value: DELETED\n      logic-not-delete-value: NOT_DELETE\n  mapper-locations: classpath*:vip/xiaonuo/**/mapping/*.xml,com/bstek/**/mapping/*.xml\n  type-handlers-package: vip.xiaonuo.common.handler\n\n#########################################\n# easy-trans configuration\n#########################################\neasy-trans:\n  is-enable-redis: true\n  is-enable-global: true\n  is-enable-tile: true\n  is-enable-cloud: false\n\n#######################################