 prefer-ip: true\n  cloud:\n    loadbalancer:\n      ribbon:\n        enabled: false\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    # nacos配置\n    nacos:\n      discovery:\n        server-addr: ${spring.cloud.nacos.config.server-addr}\n        namespace: ${spring.cloud.nacos.config.namespace}\n        group: ${spring.cloud.nacos.config.group}\n        username: ${spring.cloud.nacos.config.username}\n        password: ${spring.cloud.nacos.config.password}\n    sentinel:\n      transport:\n        dashboard: localhost:9002\n        port: 8719\n      scg:\n        fallback:\n          mode: response\n          response-status: 455\n          response-body: error!\n    # gateway配置\n    gateway:\n      default-filters:\n        - DedupeResponseHeader=Access-Control-Allow-Origin\n      globalcors:\n        add-to-simple-url-handler-mapping: true\n        corsConfigurations:\n          \'[/**]\':\n            allowed-origins-patterns: \"*\"\n            allowed-methods: \"*\"\n            allowed-headers: \"*\"\n            allow-credentials: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/api/webapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=2 #去掉Path中前两个节点\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/druid/**\n          filters:\n            - SwaggerHeaderFilter\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-biz-app\n          uri: lb://snowy-biz-app\n          predicates:\n            - Path=/api/bizapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=